import React, { useState, useEffect } from 'react';
import Axios from 'axios';

const api = 'https://coronavirus-tracker-api.herokuapp.com/v2/locations';

const totalKeyArray = ['confirmed', 'recovered', 'deaths'];

function ListView() {
  const [locationArray, setLocationArray] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(null);

  function sortedLocationArray(locations) {
    return [...locations].sort((location1, location2) => location2.latest.confirmed - location1.latest.confirmed);
  }

  useEffect(() => {
    Axios.get(api).then((response) => {
      const sortedLocations = sortedLocationArray(response.data.locations);
      setLocationArray(sortedLocations);
    }).catch((error) => {
      console.log(error);
    });
  }, []);

  const totalElements = totalKeyArray.map((key) => {
    const sum = locationArray.reduce((sum, location) => sum + location.latest[key], 0);
    return (
            <div key={key} className="columns">
                <div className="column">
                    <h6 className="title is-6">{key}</h6>
                </div>
                <div className="column">
                    <p className="is-6 has-text-right">{sum}</p>
                </div>
            </div>
    );
  });

  const locationElements = locationArray.map((location) => {
    const {
      id, country_code,
      country, province,
      latest: { confirmed },
    } = location;

    let title = country;
    if (province !== '' && province !== country) {
      title = `${province}, ${country}`;
    }

    let locationClass = 'list-view-location';
    if (selectedLocation !== null) {
      if (location.id === selectedLocation.id) {
        locationClass += ' selected';
      }
    }

    return (
            <div key={`${id}-${country_code}`} className={locationClass}>
                <div className="columns">
                    <div className="column">
                        <h6 className="title is-6">{title}</h6>
                    </div>
                    <div className="column">
                        <p className="is-6 has-text-right">{confirmed}</p>
                    </div>
                </div>
            </div>
    );
  });

  return (
        <div className="list-view">
            <div className="list-view-brand">
                <h2 className="title is-4">COVID-19 Tracker</h2>
            </div>
            <div className="list-view-total">
                <h2 className="title is-4">Total</h2>
                {totalElements}
            </div>
            <div className="list-view-locations">
                {locationElements}
            </div>
        </div>
  );
}

export default ListView;
