/* eslint-disable react/no-unescaped-entities */
/* eslint-disable no-undef */
import React from 'react';
import { divIcon, Map } from 'leaflet';
// import { MapContainer as LeafletMap,  TileLayer, Marker,  Popup } from 'react-leaflet';
import { TileLayer, Marker, Popup } from 'react-leaflet';
import './Map.css';

const icon = divIcon({className: 'marker-icon', iconSize:[32,32]});

function Maps(props) {
  const  { locationArray } = props;

  const markerElements = locationArray && locationArray.map((location) => {
    const {
      id, country_code,
      country, province,
      coordinates: { latitude, longitude },
    } = location;

     let title = country;
      if (province !== '' && province !== country) {
            title = `${province}, ${country}`;
        }

    return (<Marker  key={`${id}-${country_code}`}
                     position={[latitude, longitude]}
                     icon={icon} >
                      <Popup>{title}</Popup>
       </Marker>);
  });

  return (
    <div className='map'>
<Map center={[13, 100]}
        zoom={6}>
<TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' />

</Map>
  {markerElements}
    </div>
  );
}
export default Map;
