    //  totalRecovered={totalRecovered}
    //  totalDeaths={totalDeaths}
    //  covidSummary={covidSummary}
     country={''}
/></div> */}
<div className='cards_map'>

<MapView locationArray={locationArray} />
<DetailsView />

