import React from 'react';
import './CSS/App.scss';
// import numeral from 'numeral';
// import ArraySort from 'array-sort';
// import { Card, CardContent } from '@material-ui/core';
import axios from './axios';
import Summary from './components/Summary.jsx';
import MapView from './components/MapView';
import 'leaflet/dist/leaflet.css';

function App() {
  // const [totalConfirmed, setTotalConfirmed] = React.useState(0);
  // const [totalRecovered, setTotalRecovered] = React.useState(0);
  // const [totalDeaths, setTotalDeaths] = React.useState(0);
  // const [covidSummary, setCovidSummary] = React.useState({});
  const [locationArray, setLocationArray] = React.useState([]);
  const [loading, setLoading] = React.useState(false);

  function sortedLocationArray(locations) {
    return [...locations].sort((location1, location2) => location2.latest.confirmed - location1.latest.confirmed);
  }

  React.useEffect(() => {
    setLoading(true);
    axios.get('/v2/locations').then((res) => {
      setLoading(false);

      if (res.status === 200) {
        // setTotalConfirmed(res.data.Global.TotalConfirmed);
        // setTotalRecovered(res.data.Global.NewRecovered);
        // setTotalDeaths(res.data.Global.TotalDeaths);
        // setCovidSummary(res.data);
        setLocationArray(res.data.locations);
        // setCovidSummary = ArraySort(setCovidSummary, 'TotalConfirmed', { reverse: true });
      }
    })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  console.log(locationArray);

  if (loading) {
    return <p> Fetching!!! </p>;
  }

  return (
    <div className="App">
<div className="app__left">
 <div className="app__header">
<h1>COVID-19 Dashboard by the Center for Systems Science and Engineering
(CSSE) at Johns Hopkins University (JHU)</h1>
   </div>

<Summary
      locationArray={locationArray}

                                  />
