import React from 'react';
// import { Card, CardContent } from '@material-ui/core';
import numeral from 'numeral';
import axios from '../axios';
import InfoBox from './InfoBox';
/** @function Summary  */

function Summary() {
  const [covidSummary, setCovidSummary] = React.useState({});
  const [totalConfirmed, setTotalConfirmed] = React.useState(0);
  const [loading, setLoading] = React.useState(false);
  React.useEffect(() => {
    setLoading(true);
    axios.get('/summary')
      .then((res) => {
        setLoading(false);

        if (res.status === 200) {
          setTotalConfirmed(res.data.Global.TotalConfirmed);
          setCovidSummary(res.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  if (loading) {
    return <p> Fetching!!! </p>;
  }


  return (
<div>
        < div className='block_global'>
             <InfoBox
              title="Global Cases"
              total ={totalConfirmed}
                                     />
         <h4>{new Date().toDateString()}</h4>
            </div>

  <div className='table'>
       <h3>Cases by Country/Region/Sovereignty</h3>
           {covidSummary.Countries && covidSummary.Countries.map((country) => (
        // eslint-disable-next-line react/jsx-key
        <tr>
          <td>{country.Country}</td>
       <td>
             <strong>{numeral(country.TotalConfirmed).format(' ')}</strong>
          </td>
        </tr>
           ))}
</div>
</div>
  );
}
export default Summary;
