import './App.css';
// import numeral from 'numeral';
import { Card, CardContent } from '@material-ui/core';
import React, { useState } from 'react';
import Table1 from './table1.jsx';
import Table1p2 from './table1p2.jsx';
import useFetch from './getInfo.jsx';
import axios from './axios';
import Summary from './components/Summary.jsx';
import Count from './components/Summary.jsx';
import {
  filterInfo,
  periodInfo,
  statusInfo,
} from './table1Info';
import findCountryInfo from './findCountryInfo';
import Country from './components/Country';

function App() {
  const info = useFetch('https://api.covid19api.com/summary');
  const [countryData, setCountry] = useState('Belarus');
  const [covidSummary, setCovidSummary] = useState({});
  const [totalConfirmed, setTotalConfirmed] = useState(0);
  const [loading, setLoading] = React.useState(false);

  const filterData = (period, status) => filterInfo(info, period, status);
  const findCountry = (country) => {
    setCountry(country);
  };

  React.useEffect(() => {
    setLoading(true);
    axios.get('/summary')
      .then((res) => {
        setLoading(false);

        if (res.status === 200) {
          setTotalConfirmed(res.data.Global.TotalConfirmed);
          setCovidSummary(res.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  if (loading) {
    return <p> Fetching!!! </p>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>COVID-19 Dashboard by the Center for Systems Science and Engineering
(CSSE) at Johns Hopkins University (JHU)</h1>
      </header>
      {info && <Table1
        filterData={filterData}
        info={filterInfo(info, 0, 0)}
        periodInfo={periodInfo}
        findCountry={findCountry}
        statusInfo={statusInfo}
      />}
      {info && <Table1p2 info={findCountryInfo(info, countryData)} />}

<div className="app__stats"><Summary
    totalConfirmed={totalConfirmed}
     covidSummary={covidSummary}
/></div>

<Card className="app__right">
      <CardContent>
<div className='table'>
<Country />
       {/* <h3>Cases by Country/Region/Sovereignty</h3>
           {covidSummary.Countries && covidSummary.Countries.map((country) => (
        // eslint-disable-next-line react/jsx-key
        <tr>
          <td>{country.Country}</td>
       <td>
             <strong>{numeral(country.TotalConfirmed).format(' ')}</strong>
          </td>
        </tr>
           ))} */}
</div>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
