	}

	return (
		<div className="App">
			<ListView
				locationArray={locationArray}
				selectedLocation={selectedLocation}
				onSelectItem={onSelectLocation}
				onDeselectItem={onDeselectLocation} />
			<MapView
				locationArray={locationArray}
				mapCenter={mapCenter}
				onSelectMarker={onSelectLocation} />
			{detailsView}
		</div>
	);
}

export default App;
